mapper.select:
select count(*)
from user
where user_email = #{userEmail}

select count(*)
from user
where user_email = #{userEmail}
  and user_password = #{userPassword}

mapper.update:
update user
set use_yn = 'N'
where user_email = #{userEmail}
  and user_password = #{userPassword}

update user
set user_password = #{userPassword}
where user_email = #{userEmail}

update user
set user_cellphone = #{targetUserUserCellphone}
where user_email = #{userEmail}

mapper.insert:
insert into user ( user_email
                 , user_password
                 , user_cellphone)
values ( #{userEmail}
       , #{userPassword}
       , #{userCellphone})

mapper.update:
update user
set user_role = 'ROLE_ADMIN'
where user_email = #{userEmail}
  and 'limitedsale' = #{adminCode}
